import React from 'react';
import './Navigation.css';


// navBoi.style.backgroundColor = "red"

class Navigation extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			mouseOver: "",
		}
	}



	slideHover = () => {
		let slideBoi = document.getElementById('hover-line')
		// console.log(window.event.target.id)
		// let HeyBoi = document.getElementById('underline-div')
		// HeyBoi.style.left = "100px"
		// console.log(HeyBoi)

		let hoveredItem = window.event.target
		// console.log(hoveredItem)
		// console.log(document.getElementById('nav-skills'))

		slideBoi.style.left = `${hoveredItem.getBoundingClientRect().left}px`
		slideBoi.style.width = `${hoveredItem.getBoundingClientRect().width}px`

		// let skills = document.getElementById('nav-skills')
		// let slideBoi = document.getElementById('underline-div')
		// console.log(skills.getBoundingClientRect().left);
		// slideBoi.style.left = `${skills.getBoundingClientRect().left}px`

		// console.log(skills.offsetWidth);
		//console.log(Skills.style.color)
	}

	snapBack = (input) => {
		// console.log
		console.log(`Snap that back to ${input}`)
		// slideBoi.style.left = `${hoveredItem.getBoundingClientRect().left}px`
		// slideBoi.style.width = `${hoveredItem.getBoundingClientRect().width}px`
	}

	render () {
		const { onRouteChange } = this.props;
		return (
		<div className='NavWrapper' style={{'textAlign': 'center'}}>
			<div id="hover-line"></div>
			<div id="selection-line"></div>
			<nav className='' id='nav-wrapper' style={{'backgroundColor': document.body.style.backgroundColor}}>
				<p className="NavItem" id="nav-home" onClick={() => { onRouteChange('home'); this.snapBack(window.event.target.id) }} onMouseOver={this.slideHover} style={{'marginRight': 'auto', 'marginLeft': '0px', 'fontSize': '3em'}}>Home</p>
				<p className="NavItem" id="nav-skills" onClick={() => onRouteChange('skills')} onMouseOver={this.slideHover} >Skills</p>
				<p className="NavItem" id="nav-projects" onClick={() => onRouteChange('projects')} onMouseOver={this.slideHover}>Projects</p>
				<p className="NavItem" id="nav-contact" onClick={() => onRouteChange('contact')} onMouseOver={this.slideHover} style={{'marginRight': '0px',}} >Contact</p>
			</nav>
		</div>
	);
	}	
}
